      this.myChartObjs[6].setOption(_opt, true)
    },
    drawChart_8() {
      const links_name = ['in', 'out', 'all']
      const series = []

      for (const ln in links_name) {
        series.push(
          extendLineSeries({
            name: `${links_name[ln]}`,
            type: 'line',
            smooth: false,
            data: this.sourceData[`E_${links_name[ln]}`]
          })
        )
      }

      series.sort((x, y) => {
        return y.data.slice(-1) - x.data.slice(-1)
      })
      const _opt = extendEchartsOpts({
        title: {
          text: '网络标准结构熵 E',
          left: '35%'
        },
        legend: {
          data: series.map(el => el.name)
        },
        xAxis: {
          name: 'Year',
          type: 'category',
          boundaryGap: false,
          data: _.range(1900, 2021)
        },
        yAxis: {
          name: 'e',
          type: 'value',
          'min': 0.8
        },
        series: series
      })
      this.myChartObjs[7].setOption(_opt, true)
    },
    drawChart_9() {
      const links_name = ['direct', 'undirect']
      const series = []

      for (const ln in links_name) {
        series.push(
          extendLineSeries({
            name: `${links_name[ln]}`,
            type: 'line',
            smooth: false,
            data: this.sourceData_2[`${links_name[ln]}_d`]
          })
        )
      }

      series.sort((x, y) => {
        return y.data.slice(-1) - x.data.slice(-1)
      })
      const _opt = extendEchartsOpts({
        title: {
          text: '网络密度',
          left: '35%'
        },
        legend: {
          data: series.map(el => el.name)
        },
        xAxis: {
          name: 'Year',
          type: 'category',
          boundaryGap: false,
          data: _.range(1900, 2021)
        },
        yAxis: {
          name: 'density',
          type: 'value'
          // 'min': 0.8
        },
        series: series
      })
      this.myChartObjs[8].setOption(_opt, true)
    },
    async getData() {
      this.drawChart_1()
      this.drawChart_2()
      this.drawChart_3()
      this.drawChart_4()
      this.drawChart_5()
      this.drawChart_6()
      this.drawChart_7()
      this.drawChart_8()
      this.drawChart_9()
    }

  }
}
</script>
