      const series = []

      for (const qi of quart_split) {
        for (const ln of links_name) {
          series.push(
            extendLineSeries({
              name: `${ln}-${qi}`,
              type: 'line',
              smooth: false,
              data: this.sourceData_1[`quart-${qi}-${ln}`]
            })
          )
        }
      }

      series.sort((x, y) => {
        return y.data.slice(-1) - x.data.slice(-1)
      })
      console.log(series)
      const _opt = extendEchartsOpts({
