          left: '35%'
        },
        legend: {
          data: series.map(el => el.name)
        },
        xAxis: {
          name: 'Year',
          type: 'category',
          boundaryGap: false,
          data: _.range(1900, 2021)
        },
        yAxis: {
          name: 'links count',
          type: 'value'
        },
        series: series
      })
      this.myChartObjs[0].setOption(_opt, true)
